import React, { useState } from 'react';
import axios from 'axios';

const WhatsAppForm = () => {
  const [recipientNumber, setRecipientNumber] = useState('');
  const [message, setMessage] = useState('');

  const handleSend = async () => {
    try {
      const response = await axios.post(
        'https://graph.facebook.com/v17.0/105954558954427/messages',
        {
          messaging_product: 'whatsapp',
          to: recipientNumber,
          type: 'template',
          template: {
            name: 'hello_world',
            language: { code: 'en_US' },
          },
        },
        {
          headers: {
            Authorization: 'Bearer EAABwngny5GUBAN5nkZCkfSt4zCrsI8IvANifugStd98ZBMUMuGBnfuuQBkXUI8NdXoAtbv9hYjGdK4YaScQUGYgbY6S1RbLMReOXttSPVFeildZB2PCEGL3ZCBYCbeThELc7b2qYcNYroCrjyZA4iZAZBy9KRhNsz5ZByktD9f7TiEiQBgZCX3taKTPhs9lBELp4LOuiPB0Lx9AZDZD',
            'Content-Type': 'application/json',
          },
        }
      );
      console.log(response.data);
      alert('Message sent successfully!');
    } catch (error) {
      console.error('Error sending message:', error);
      alert('Failed to send message. Please try again later.');
    }
  };

  return (
    <div>
      <label htmlFor="recipientNumber">Recipient Number:</label>
      <input
        type="text"
        id="recipientNumber"
        value={recipientNumber}
        onChange={(e) => setRecipientNumber(e.target.value)}
      />
      <br />
      <label htmlFor="message">Message:</label>
      <textarea
        id="message"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <br />
      <button onClick={handleSend}>Send</button>
    </div>
  );
};

export default WhatsAppForm;
