import React from "react";

function App() {
  return (
    <center>
      <div>
        <h1 class="text-green text-3xl font-bold">GeeksforGeeks</h1>
        <h3 class="text-xl text-black">
          Scale Animation on Hover using Tailwind CSS
        </h3>
      </div>
      <div>
      <div
        href="##"
        className="bg-white overflow-hidden drop-shadow-md dark:drop-shadow-xl w-96 h-72 rounded-md items-center justify-center"
      >
        <div className="w-full bg-cover overflow-hidden">
          <img src="https://media.geeksforgeeks.org/wp-content/uploads/20220221132017/download.png" alt="" className="w-full h-56 transform transition duration-1000 hover:scale-125" />
        </div>
        <div class="flex mt-4 justify-between px-4">
          <h3 className="font-bold text-md">
            GeeksforGeeks
          </h3>
          <a
            href="https://geeksforgeeks.org"
            class="items-center py-2 px-4 text-sm font-medium text-center text-gray-900 bg-white rounded-lg border border-sky-400 hover:bg-sky-300"
          >
            Visit
          </a>
        </div>
      </div>
      </div>
    </center>
  );
}

export default App;
