import React from "react";
import { Helmet } from "react-helmet";
import "../App.css";
import { useState } from "react";
import { useEffect } from "react";

const papers = [
  {
    title: "Paper Title 1",
    authors: ["Author 1", "Author 2"],
    publicationDate: "May 1, 2023",
    abstract:
      "This is the abstract of Paper 1. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer nec odio. Praesent libero. Sed cursus ante dapibus diam.",
    link: "https://example.com/paper-1",
  },
  {
    title: "Paper Title 2",
    authors: ["Author 3", "Author 4"],
    publicationDate: "April 15, 2023",
    abstract:
      "This is the abstract of Paper 2. Lorem ipsum dolor sit amet, consectetur adipiscing elit. Integer nec odio. Praesent libero. Sed cursus ante dapibus diam.",
    link: "https://example.com/paper-2",
  },
  // more paper objects here
];

function Research() {

    const [paperViews, setPaperViews] = useState({});

  const handleView = (link) => {
    setPaperViews((prevViews) => ({ ...prevViews, [link]: (prevViews[link] || 0) + 1 }));
  };

  const Paper = ({ title, authors, publicationDate, abstract, link, downloadLink }) => {
    const [currentViews, setViews] = useState(0);
  
    useEffect(() => {
      const storedViews = localStorage.getItem(link);
      if (storedViews) {
        setViews(parseInt(storedViews));
      }
    }, [link]);
  
    const handleView = () => {
      const newViews = currentViews + 1;
      setViews(newViews);
      localStorage.setItem(link, newViews.toString());
    };
  
    return (
      <div className="mb-8">
        <a href={link} onClick={handleView} className="text-lg font-medium text-gray-900 hover:underline">
          {title}
        </a>
        <span className="ml-2 text-sm text-gray-500">{currentViews} views</span>
        <div className="mt-1 text-sm text-gray-500">{authors.join(', ')}</div>
        <div className="mt-1 text-sm text-gray-500">{publicationDate}</div>
        <div className="mt-2 text-sm text-gray-500">{abstract}</div>
        <div className="mt-4 flex items-center">
          <a href={downloadLink} className="inline-flex items-center px-4 py-2 border border-gray-300 rounded-md shadow-sm text-sm font-medium text-blue-600 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500">
            Download
          </a>
        </div>
      </div>
    );
  }; 

  return (
    <>
      <Helmet>
        <title>Research | Tarun Singh</title>
      </Helmet>
      <div className="bg-white dark:bg-black h-screen">
        <div className="flex items-center justify-center">
          <h1 data-aos="fade-right" className="text-5xl font-bold text-blue">
            Research Publications
          </h1>
        </div>

        <div className="max-w-4xl mx-auto py-8 px-4 sm:py-16 sm:px-6 lg:px-8">
      <h1 className="text-3xl font-bold text-gray-900 mb-8">Machine Learning Papers</h1>
      {papers.map((paper) => (
        <Paper key={paper.title} {...paper} />
      ))}
    </div>
      </div>
    </>
  );
}

export default Research;
