import React from "react";

const data = [
    {
        header: "Front End",
        body: {
            item1: "",
            item2: "",
            item3: "",
        },
        imgUrl: ""
    }
]

function Hero({header, body, imgUrl}) {
  return (
    <div className="flex flex-col md:flex-row gap-8 text-center">
      <div className="pb-10">
        <h1 className="text-6xl font-bold text-gray-900 dark:text-white">
          {data.header}
        </h1>
        <div className="text-left px-8 py-4">
          <ul className="list-none lg:text-2xl md:text-xl sm:text-lg dark:text-gray-400">
            <li>⚡Building resposive website front end using React</li>
            <li>
              ⚡Designing UI using Tailwind CSS, Bootstrap, and Material UI
            </li>
          </ul>
        </div>
      </div>
      <div className="pb-10">
        <h1 className="text-6xl font-bold text-gray-900 dark:text-white">
          Front-end Development
        </h1>
      </div>
    </div>
  );
}

export default Hero;
