import React from "react";
import { Link } from "react-router-dom";
import avatar from "../Assets/avatar.png";
import { Helmet } from "react-helmet";

import "../App.css";
import Hero from "../Components/Hero";

import AppDev from '../Assets/appdev.svg';
import Programming from "../Assets/programming.svg";
import Writer from "../Assets/writer.svg";

import ScrollAnimation from 'react-animate-on-scroll';

function Home() {
  return (
    <>
      <Helmet>
        <title>Home | Tarun Singh</title>
      </Helmet>
      <div class="bg-white dark:bg-black px-8 h-screen">
        <section className="mx-auto container relative z-0 px-1 xl:px-0">
          <div className="flex flex-col-reverse md:flex-row">
            <div className="md:w-4/5 md:pt-24 pb-10 lg:py-32 xl:py-48">
              <h1 className="text-5xl lg:text-6xl xl:text-8xl font-black tracking-normal dark:text-white text-gray-900 text-center md:text-left f-f-i md:w-10/12 text-heading-color">
                HiðŸ‘‹, <br /> I'm Tarun Singh{" "}
              </h1>
              <h2 className="md:w-8/12 py-4 text-center md:text-left md:py-8 dark:text-gray-400 text-gray-700 text-2xl lg:text-2xl">
                Software Developer, Technical Writer, YouTuber, and Freelancer
                at{" "}
                <span className="bg-blue text-white px-1 rounded">
                  GeeksforGeeks
                </span>
              </h2>
              <Link to="/about" className="w-full flex justify-center md:block">
                <button className="hover:bg-blue hover:text-white py-3 px-10 lg:py-7 lg:px-20 rounded-full dark:drop-shadow-xl border-2">
                  <h3 className="text-xl dark:text-white font-bold uppercase">About</h3>
                </button>
              </Link>
            </div>
            <div className="w-4/5 sm:w-4/5 md:h-auto lg:-mr-96 m-auto flex items-center overflow-hidden">
              <img
                className="md:absolute md:w-2/4 md:-ml-28"
                src={avatar}
                alt=""
              />
            </div>
          </div>
        </section>
        <ScrollAnimation animateIn="fadeIn" duration={10} className="mx-auto container h-screen mt-20">
          <h1 className="lg:text-7xl md:text-5xl sm:text-4xl text-center text-heading-color tracking-normal font-bold text-gray-900 dark:text-white">What I Do?</h1>
          
        </ScrollAnimation>
      </div>
    </>
  );
}

export default Home;
