import React from "react";


const data = [
  {
    Key: 1,
    Item: ['List1', 'List2', 'List2']
  },
  {
    Key: 2,
    Item: ["Listing 3", "Listing 4"]
  },
];

const items = data.map((data, Key) => {
  return (
    <div>
      <li key={Key}>⚡{data.Item}</li>
      <li key={Key}>⚡{data.Item}</li>
    </div>
  )
})


function Hero({header, body, imgUrl}) {
  return (
    <div className="flex flex-col md:flex-row gap-16 text-center mb-10">
      <div className="pb-10">
        <h1 className="text-6xl font-bold text-blue dark:text-white text-left">
          {header}
        </h1>
        <div className="text-left px-8 py-4">
          <ul className="list-none lg:text-2xl md:text-xl sm:text-lg dark:text-gray-400">
            {/* <li>⚡Building resposive website front end using React</li>
            <li>
              ⚡Designing UI using Tailwind CSS, Bootstrap, and Material UI
            </li> */}
            {items}
          </ul>
        </div>
      </div>
      <div className="pb-10">
        <img src={imgUrl} alt="hero" width={600} />
      </div>
    </div>
  );
}

export default Hero;
