import { useState, useRef, useEffect } from "react";

function App() {
  const [isIntersecting, setIsIntersecting] = useState(false);
  const ref = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsIntersecting(entry.isIntersecting);
      },
      { rootMargin: "-300px" }
    );

    observer.observe(ref.current);

    return () => observer.disconnect();
  }, []);

  useEffect(() => {
    if (isIntersecting) {
      ref.current.querySelectorAll("div").forEach((el) => {
        el.classList.add("slide-in");
      });
    } else {
      ref.current.querySelectorAll("div").forEach((el) => {
        el.classList.remove("slide-in");
      });
    }
  }, [isIntersecting]);

  return (
    <div className="App">
      <header className="h-20 bg-gray-200">This is the Header</header>
      <main ref={ref} className="h-screen flex items-center justify-center">
        <div className="child-one bg-blue-200 w-64 h-64 m-4">
          Child One
        </div>
        <div className="child-two bg-green-200 w-64 h-64 m-4">
          Child Two
        </div>
      </main>
      <footer className="h-20 bg-gray-200">This is the Footer</footer>
    </div>
  );
}

export default App;
