import React from "react";

function ListItem({ logo, title, company, date, location, description }) {
  return (
    <div className="flex flex-col md:flex-row bg-white dark:bg-[#111111] p-4 border border-gray-800 rounded-lg gap-4 mb-6 mt-6 transform transition duration-1000 hover:scale-95 items-center">
      {/* Logo here */}
      <div className="w-1/12 sm:w-44">
        <img src={logo} alt="logo" className="w-24 h-24 rounded-full" />
      </div>
      {/* Content goes here */}
      <div className="w-10/12 sm:w-fit px-2">
        <div className="flex flex-row justify-between">
          <div className="flex flex-col">
            <h1 className="text-lg font-bold dark:text-blue">{title}</h1>
            <h2 className="text-lg text-gray-800 dark:text-[#D1D5DB]">{company}</h2>
          </div>
          <div className="flex flex-col">
            <h3 className="text-sm text-gray-400">{date}</h3>
            <h3 className="text-sm text-gray-400">{location}</h3>
          </div>
        </div>
        <p className="text-sm text-gray-900 dark:text-[#D1D5DB]">
          {description}
        </p>
      </div>
    </div>
  );
}

export default ListItem;
