import React, { useState } from "react";
import { Helmet } from "react-helmet";
import ListItem from "../Components/ListItem";

import gfg from "../Assets/gfg.png";
import cm from "../Assets/cm.jpg";
import gssoc from "../Assets/gssoc.png";

function Experience() {
  const [open, setOpen] = useState(false);

  const toggle = () => {
    setOpen(!open);
  };

  return (
    <>
      <Helmet>
        <title>Experience | Tarun Singh</title>
      </Helmet>
      <div className="bg-white dark:bg-black h-screen">
        <div className="flex items-center justify-center">
          <h1 data-aos="fade-in" className="text-5xl font-bold text-blue">
            Experience
          </h1>
        </div>
        <div
          className="bg-gray-100 mt-10 p-4 container mx-auto hover:cursor-pointer text-xl"
          onClick={toggle}
        >
            Experience
          {open && (
            <div 
            data-aos="fade-down"
            data-aos-duration="400"
            data-aos-offset="100"
            data-aos-delay="50"
            data-aos-easing="ease-in-sine"
            >
              <ListItem
                logo={cm}
                title="Technical Content Writer"
                company="Coding Minutes"
                date="Sep 2022 - Present"
                description="Coding Minutes was started with a mission to provide affordable & high quality education for everyone. With Udemy as our technology partner, we wish to bring all our courses with top-notch content to our students at pocket friendly prices with lifetime access."
                location="Remote"
              />
              <ListItem
                logo={gssoc}
                title="Mentor"
                company="Girlscript Summer of Code"
                date="Sep 2020 - Present"
                description="Mentored students in Girlscript Summer of Code for Open Source project for
Social Good. Reviewed various codes and GitHub repoâ€™s of participants related to HTML/CSS,
Bootstrap, and React etc."
                location="Remote"
              />
              <ListItem
                logo={gfg}
                title="Technical Content Writer"
                company="GeeksforGeeks"
                date="Oct 2020 - Aug 2021"
                description="Selected as the Geek of the Month for writing informational and quality articles
throughout the month and also for publishing maximum no. of articles in a month. Written over 300+ Technical articles including React based frameworks, CSS
based Frameworks & Libraries, and HTML/CSS & JS etc."
                location="Remote"
              />
            </div>
          )}
        </div>
      </div>
    </>
  );
}

export default Experience;
